import './App.css';
import { useState,useEffect} from 'react';

function App() {
const [fruit,setFruit] = useState([])
const [ladowanie,setLadowanie] = useState(true)
const [error,setError] = useState(false)
useEffect(()=>{
    const getdata = async ()=>{
      try{
      const data = await fetch(`https://cors-anywhere.herokuapp.com/https://www.fruityvice.com/api/fruit/all`)
      const json = await data.json()
      setFruit(json)
      setLadowanie(false)
      console.log(json)
      }catch(err){
        console.log(err)
        setError(true)
      }
    }
    getdata()
  },[])
  return (
    <>
    {error && "Wystąpił błąd podczas ładowania danych"}
    {ladowanie?"Ładowanie danych...":(
    <div className="App flex flex-row flex-wrap w-full">
      {fruit.map((item,idx)=>(
        <div key={idx} className='flex justify-center items-center relative flex-col w-[110px] h-[130px] m-3 border-black border-2 rounded-md'>
          <h1 className='flex font-bold'>{item.name}</h1>
          <p>Kalorie: {item.nutritions.calories}</p>
          <p>Tłuszcz: {item.nutritions.fat}g</p>
          <p>Cukier: {item.nutritions.sugar}g</p>
          </div>
      ))}
    </div>)}
    </>
  );
}

export default App;
